"use strict";function topMenuHandle(){for(var e=config.topMenu,t=[],n=0;n<e.length;n++){var a='<div class="item" data-index="'+e[n].code+'">'+e[n].desc+"</div>";t.push(a)}$(".top-nav").html(t.join("")),$(".top-nav").off("click",".item").on("click",".item",function(){$(this).siblings(".item").removeClass("active"),$(this).addClass("active");var e="views/"+$(this).data("index")+".html";$("main").load(e)}),$(".top-nav").find(".item").eq(0).trigger("click")}function showDate(){var e=moment().format("YYYY-MM-DD"),t=moment().format("HH:mm"),n='<span style="">'+e+"</span><span>"+changeWeek(moment().format("d"))+"</span><span>"+t+"</span>";$(".now-date").html(n)}function changeWeek(e){switch(e){case"1":return"星期一";case"2":return"星期二";case"3":return"星期三";case"4":return"星期四";case"5":return"星期五";case"6":return"星期六";case"7":return"星期日"}}$(function(){topMenuHandle(),showDate(),setInterval(function(){showDate()},1e3)});